sys_ux_data_broker_transform.required_translations:
[ {
  "message" : "Transforming JSON array into array for dropdown",
  "comment" : ""
}, {
  "message" : "color",
  "comment" : ""
}, {
  "message" : "refined data",
  "comment" : ""
} ]

sys_ux_data_broker_transform.script:
function transform(input){
	var oldName = input.refinedData;
	var color = input.color;
	var newName = "Hello "+oldName+ "! the color you've selected is "+color;


 return newName;
}
